Template.addGroup.events({
	'submit .add-group': function(e) {
		e.preventDefault();
		var groupName = $("#groupName").val();
		var currentUser = Meteor.user().username;
		e.stopPropagation();

		Meteor.call("addGroup",groupName,currentUser);

	}
})
